def getVestingClass(vesting_type):
	if vesting_type == INITIAL_100 or vesting_type == MYCELIUM_VESTING:
		return SIMPLE_VESTING
	elif vesting_type == STANDARD_VESTING or vesting_type == GOVERNOR_VESTING or vesting_type == MYCELIUM_EMPLOYEE_VESTING:
		return COMPLEX_VESTING
	else:
		return NO_VESTING